import React from 'react'; 

class Register  extends  React.Component{
    constructor(props){
        super(props);
        this.state ={
            email: '',
            password: '', 
            name: ''
        }
    }

    onEmailChange = (event) =>{
        this.setState({email: event.target.value})
    }

    onPasswordChange = (event) =>{
        this.setState({password: event.target.value})
    }
    onNameChange = (event) =>{
        this.setState({name: event.target.value})
    }
    onSubmitRegister = () =>{
        fetch('http://localhost:8000/register', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.email,
                password: this.state.password,
                name: this.state.name
            })
        })
        .then(response => response.json())
        .then(user =>{
            if(user){
                this.props.loadUser(user)
                this.props.cambioDeRuta('home'); 
            }
        })
    }

 
    render(){
        return (
            <div>
                <main className="pa4 black-80">
                    <div className="measure center bg-white pa5 .shadow-2 trans">
                    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                         <legend className="f4 fw6 ph0 mh0">Registro</legend>
                         <div className="mt3">
                            <label className="db fw6 lh-copy f6" htmlfor="name">Nombre</label>
                            <input 
                            onChange = {this.onNameChange}
                            className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name"  id="name"/>
                        </div>
                        <div className="mt3">
                            <label className="db fw6 lh-copy f6" hmltFor="email-address">Correo</label>
                            <input 
                            onChange= {this.onEmailChange}
                            className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address"/>
                        </div>
                        <div className="mv3">
                            <label className="db fw6 lh-copy f6" for="password">Password</label>
                            <input 
                            onChange ={ this.onPasswordChange}
                            className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password"/>
                        </div>
                    </fieldset>
                    <div className="">
                         <input 
                         onClick={this.onSubmitRegister}
                         className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" 
                         value="Registrar"/>
                    </div>
                   
                    </div>
                </main>
    
            </div>
        ) 
    }
    
}

export default Register;
