import React from 'react'; 

class Register  extends  React.Component{
    constructor(props){
        super(props);
        this.state ={
            signInEmail: '',
            signInPassword: ''
        }
    }

    onEmailChange = (event) =>{
        this.setState({signInEmail: event.target.value})
    }

    onPasswordChange = (event) =>{
        this.setState({signInPassword: event.target.value})
    }

    onSubmitSignIn = () =>{
        fetch('http://localhost:8000/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
        .then(response => response.json())
        .then(data =>{
            if(data === 'éxito'){
                this.props.cambioDeRuta('home'); 
            }
        })
    }
 
    render(){
        return (
            <div>
                <main className="pa4 black-80">
                    <div className="measure center bg-white pa5 .shadow-2 trans">
                    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                         <legend className="f4 fw6 ph0 mh0">Registro</legend>
                         <div className="mt3">
                            <label className="db fw6 lh-copy f6" for="name">Nombre</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name"  id="name"/>
                        </div>
                        <div className="mt3">
                            <label className="db fw6 lh-copy f6" for="email-address">Correo</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address"/>
                        </div>
                        <div className="mv3">
                            <label className="db fw6 lh-copy f6" for="password">Password</label>
                            <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password"/>
                        </div>
                    </fieldset>
                    <div className="">
                         <input 
                         onClick={()=> cambioDeRuta('home')}
                         className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" 
                         value="Registrar"/>
                    </div>
                   
                    </div>
                </main>
    
            </div>
        ) 
    }
    
}

export default Register;
